create table `users` (`id` bigint unsigned not null auto_increment primary key, `username` varchar(50) not null, `password` varchar(256) not null, `is_admin_approved` tinyint not null, `created_at` timestamp not null default CURRENT_TIMESTAMP, `updated_at` timestamp not null default CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP) default character set utf8mb4 collate 'utf8mb4_unicode_ci';
create table `posts` (`id` bigint unsigned not null auto_increment primary key, `title` varchar(256) not null, `url` varchar(256) not null, `description` varchar(2048) not null, `user_id` bigint unsigned not null, `created_at` timestamp not null default CURRENT_TIMESTAMP, `updated_at` timestamp not null default CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP) default character set utf8mb4 collate 'utf8mb4_unicode_ci';
alter table `posts` add constraint `posts_user_id_foreign` foreign key (`user_id`) references `users` (`id`) on delete cascade;
create table `contacts` (`id` bigint unsigned not null auto_increment primary key, `name` varchar(50) not null, `email` varchar(50) not null, `phone` varchar(25) not null, `message` varchar(2048) not null, `created_at` timestamp not null default CURRENT_TIMESTAMP, `updated_at` timestamp not null default CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP) default character set utf8mb4 collate 'utf8mb4_unicode_ci';
create table `admin` (`id` bigint unsigned not null auto_increment primary key, `username` varchar(50) not null, `password` varchar(256) not null, `created_at` timestamp not null default CURRENT_TIMESTAMP, `updated_at` timestamp not null default CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP) default character set utf8mb4 collate 'utf8mb4_unicode_ci';