create table `users` (`id` bigint unsigned not null auto_increment primary key, `username` varchar(50) not null, `password` varchar(256) not null, `is_admin_approved` tinyint not null, `created_at` timestamp null, `updated_at` timestamp null);
create table `posts` (`id` bigint unsigned not null auto_increment primary key, `title` varchar(256) not null, `url` varchar(256) not null, `description` varchar(2048) not null, `user_id` bigint unsigned not null, `created_at` timestamp null, `updated_at` timestamp null);
alter table `posts` add constraint `posts_user_id_foreign` foreign key (`user_id`) references `users` (`id`) on delete cascade